if (msg.payload && msg.payload.data) {
    // Extract the data field from the message
    return {
        payload: msg.payload.data
    };
} else if (typeof msg.payload === 'string') {
    // Handle case where payload is a JSON string
    try {
        const parsed = JSON.parse(msg.payload);
        if (parsed.data) {
            return {
                payload: parsed.data
            };
        }
    } catch (e) {
        node.error("Failed to parse JSON: " + e.message, msg);
    }
}

// Return original message if we couldn't extract data
node.warn("Could not extract data field from message");
return msg;
